meta.script:
$(document).ready(function(){
	var hori=$.hori;
	/*设置标题*/
	hori.setHeaderTitle("待办");
});

var npage = 1;
var ncount = npage+20;
function fetch(){
	$.hori.showLoading();
	npage = npage+1;
	var itcode = "oadesigner";
	var oaServerName=$.cookie("oaServerName");
	var url = "/view/oamobile/todosmobilesub/Produce/DigiFlowMobile.nsf/agGetMsgViewData?openagent&login&0.47540903102505816&server="+oaServerName+"&dbpath=DFMessage/dfmsg_"+itcode+".nsf&view=vwTaskUnDoneForMobile&thclass=&page="+npage+"&count="+ncount;
	$.ajax({
		type: "get", url: url,
		success: function(response){
			//$.mobile.hidePageLoadingMsg();
			$("#more").remove();
			$("ul").append(response);
			$("ul").listview('refresh');
			$.hori.hideLoading();
		},
		error:function(response){
			//$.mobile.hidePageLoadingMsg();
			$.hori.hideLoading();
			alert("错误:"+response.responseText);
		}
	});
}
